mapper.delete:
delete from book_collect
where coll_user_id = #{collUserId,jdbcType=INTEGER}
and bct_book_id = #{bctBookId,jdbcType=INTEGER}

    and bct_user_id = #{bctUserId,jdbcType=INTEGER}

mapper.select:
select count(*) from book_collect
where coll_user_id = #{collUserId,jdbcType=INTEGER}
and bct_book_id = #{bctBookId,jdbcType=INTEGER}

    and bct_user_id = #{bctUserId,jdbcType=INTEGER}
